ST.xhtml:
(* SWITCH POSITION UPDATE *)
(* ---------------------- *)

FOR sw_ind := 1 TO variable_list.nSwitch DO
	switch_mov_to_int_TIMER[sw_ind](
		IN := (		variable_list.switch_command_right[sw_ind] AND NOT(motor_failure_switch[sw_ind]) AND Switch_pos[sw_ind] = SW_POS_t.LEFT
			  OR 	variable_list.switch_command_left[sw_ind] AND NOT(motor_failure_switch[sw_ind]) AND Switch_pos[sw_ind] = SW_POS_t.RIGHT)
		, PT := DELAY_TO_INTERMEDIATE);
	switch_mov_to_left_TIMER[sw_ind](
		IN := variable_list.switch_command_left[sw_ind] AND NOT(motor_failure_switch[sw_ind]) AND Switch_pos[sw_ind] = SW_POS_t.INTERMEDIATE
		, PT := DELAY_TO_LEFTRIGHT);
	switch_mov_to_right_TIMER[sw_ind](
		IN := variable_list.switch_command_right[sw_ind] AND NOT(motor_failure_switch[sw_ind]) AND Switch_pos[sw_ind] = SW_POS_t.INTERMEDIATE
		, PT := DELAY_TO_LEFTRIGHT);
END_FOR

FOR sw_ind := 1 TO variable_list.nSwitch DO
	IF switch_mov_to_int_TIMER[sw_ind].Q THEN
		Switch_pos[sw_ind] := SW_POS_t.INTERMEDIATE;
	ELSE IF switch_mov_to_left_TIMER[sw_ind].Q THEN
		Switch_pos[sw_ind] := SW_POS_t.LEFT;
	ELSE IF switch_mov_to_right_TIMER[sw_ind].Q THEN
		Switch_pos[sw_ind] := SW_POS_t.RIGHT;
	END_IF END_IF END_IF
END_FOR

(* TRAINS POSITION UPDATE *)
(* ---------------------- *)

FOR train_ind := 1 TO nTrains DO
	train_moved[train_ind] := FALSE;
END_FOR

(* TODO empecher les trains de se superposer
FOR tc_ind := 1 TO variable_list.nTC DO
	occup_TC[tc_ind] := 0;
	FOR train_ind := 1 TO nTrains DO
		IF Trains[train_ind].front_pos = tc_ind AND Trains[train_ind].rear_pos = tc_ind THEN
			occup_TC[tc_ind] := 4;
		ELSE IF Trains[train_ind].front_pos = tc_ind AND NOT(Trains[train_ind].rear_pos = tc_ind) THEN
