				occup_TC[tc_ind] := 4;
			ELSE
				occup_TC[tc_ind] := 1;
			END_IF
		ELSE IF NOT(Trains[train_ind].front_pos = tc_ind) AND Trains[train_ind].rear_pos = tc_ind THEN
			IF occup_TC[tc_ind] = 1 THEN
				occup_TC[tc_ind] := 4;
			ELSE
				occup_TC[tc_ind] := 2;
			END_IF
		END_IF END_IF END_IF
	END_FOR
END_FOR
