(* ENTERING TRAINS *)
(* --------------- *)

FOR limit_ind := 1 TO nEntryPoints DO
	IF send_train[limit_ind] AND NOT(send_train_prev[limit_ind]) (* AND NOT(entry_points[limit_ind].tc) TODO*) THEN
		new_entry := TRUE;
		FOR train_ind := 1 TO nTrains DO
			IF NOT(Trains[train_ind].exists) AND new_entry THEN
				new_entry := FALSE;
				Trains[train_ind].exists := TRUE;
				Trains[train_ind].rear_pos := Next_TC(entry_points[limit_ind].tc, Opposite_dir(entry_points[limit_ind].orientation), Switch_pos);
				Trains[train_ind].front_pos := entry_points[limit_ind].tc;
				Trains[train_ind].orientation := entry_points[limit_ind].orientation;
				train_moved[train_ind] := TRUE;


			END_IF
		END_FOR
	END_IF
	send_train_prev[limit_ind] := send_train[limit_ind];
END_FOR

(* TRAIN TURN BACK *)
(* --------------- *)

FOR train_ind := 1 TO nTrains DO
	IF Trains[train_ind].exists AND Trains[train_ind].stopped THEN
		IF (	Trains[train_ind].front_pos = TC.TC_108
			AND Trains[train_ind].rear_pos = TC.TC_108
			AND	train_reverse_TC_108 AND NOT(train_reverse_TC_108_prev)) THEN
			Trains[train_ind].orientation := Opposite_dir(Trains[train_ind].orientation);
		END_IF
		IF (	Trains[train_ind].front_pos = TC.TC_111
			AND Trains[train_ind].rear_pos = TC.TC_111
			AND	train_reverse_TC_111 AND NOT(train_reverse_TC_111_prev)) THEN
			Trains[train_ind].orientation := Opposite_dir(Trains[train_ind].orientation);
		END_IF
	END_IF
END_FOR

train_reverse_TC_108_prev := train_reverse_TC_108;
train_reverse_TC_111_prev := train_reverse_TC_111;

(* TRAIN MOVEMENT TIMERS *)
(* --------------------- *)

FOR train_ind := 1 TO nTrains DO
	move_train_TIMER[train_ind](IN := Trains[train_ind].exists AND NOT(train_moved_prev[train_ind]) AND NOT(Trains[train_ind].stopped), PT := DELAY_TRAIN_MOV);
END_FOR

(* EXITING TRAINS *)
(* -------------- *)

FOR limit_ind := 1 TO nExitPoints DO
	FOR train_ind := 1 TO nTrains DO
		IF Trains[train_ind].exists THEN
			IF (Trains[train_ind].rear_pos = exit_points[limit_ind].tc
				AND	Trains[train_ind].front_pos = Next_TC(exit_points[limit_ind].tc, exit_points[limit_ind].orientation, Switch_pos)
				AND	Trains[train_ind].orientation = exit_points[limit_ind].orientation
				AND NOT(Trains[train_ind].stopped)
				AND move_train_TIMER[train_ind].Q) THEN
					Trains[train_ind].exists := FALSE;
					Trains[train_ind].rear_pos := TC.NIL;
					Trains[train_ind].front_pos := TC.NIL;
					Trains[train_ind].orientation := DIR.NIL;
			END_IF
		END_IF
	END_FOR
END_FOR

IF evacuation THEN
	FOR train_ind := 1 TO nTrains DO
		Trains[train_ind].exists := FALSE;
		Trains[train_ind].rear_pos := TC.NIL;
		Trains[train_ind].front_pos := TC.NIL;
		Trains[train_ind].orientation := DIR.NIL;
	END_FOR
END_IF

(* TRAIN MOVEMENT *)
(* -------------- *)

FOR train_ind := 1 TO nTrains DO
	IF Trains[train_ind].exists AND move_train_TIMER[train_ind].Q THEN
		IF Trains[train_ind].rear_pos = Trains[train_ind].front_pos THEN
			IF (	NOT(Trains[train_ind].front_pos = TC.TC_103 AND Trains[train_ind].orientation = DIR.UP AND NOT(variable_list.signal_open_route_201))
				AND	NOT(Trains[train_ind].front_pos = TC.TC_107 AND Trains[train_ind].orientation = DIR.UP AND NOT(variable_list.signal_open_route_203))
				AND	NOT(Trains[train_ind].front_pos = TC.TC_108 AND Trains[train_ind].orientation = DIR.UP AND NOT(variable_list.signal_open_route_205))
				AND	NOT(Trains[train_ind].front_pos = TC.TC_111 AND Trains[train_ind].orientation = DIR.DOWN AND NOT(variable_list.signal_open_route_206))
				AND	NOT(Trains[train_ind].front_pos = TC.TC_112 AND Trains[train_ind].orientation = DIR.DOWN AND NOT(variable_list.signal_open_route_208))
				AND	NOT(Trains[train_ind].front_pos = TC.TC_107 AND Trains[train_ind].orientation = DIR.DOWN)
				) THEN
				Trains[train_ind].front_pos := Next_TC(Trains[train_ind].front_pos, Trains[train_ind].orientation, Switch_pos);
				train_moved[train_ind] := TRUE;
			END_IF
		ELSE IF Trains[train_ind].front_pos = Next_TC(Trains[train_ind].rear_pos, Trains[train_ind].orientation, Switch_pos) THEN
			Trains[train_ind].rear_pos := Trains[train_ind].front_pos;
			train_moved[train_ind] := TRUE;
		END_IF END_IF
	END_IF
END_FOR

FOR train_ind := 1 TO nTrains DO
	train_moved_prev[train_ind] := train_moved[train_ind];
END_FOR


(* INPUTS OF PLC_PRG *)
(* ----------------- *)

(* Assume switch detection failure never report erroneous Left/Right positions *)
variable_list.switch_detected_left[SWITCH.Sw_01] := NOT(detect_failure_switch[SWITCH.Sw_01]) AND (Switch_pos[SWITCH.Sw_01] = SW_POS_t.LEFT);
variable_list.switch_detected_right[SWITCH.Sw_01] := NOT(detect_failure_switch[SWITCH.Sw_01]) AND (Switch_pos[SWITCH.Sw_01] = SW_POS_t.RIGHT);
variable_list.switch_detected_left[SWITCH.Sw_02] := NOT(detect_failure_switch[SWITCH.Sw_02]) AND (Switch_pos[SWITCH.Sw_02] = SW_POS_t.LEFT);
variable_list.switch_detected_right[SWITCH.Sw_02] := NOT(detect_failure_switch[SWITCH.Sw_02]) AND (Switch_pos[SWITCH.Sw_02] = SW_POS_t.RIGHT);

FOR tc_ind := 1 TO variable_list.nTC DO
	IF failure_TC[tc_ind] THEN
		variable_list.TC_occupied[tc_ind] := TRUE; (* Assume only right-side failures *)
	ELSE
		tempBool := FALSE;
		FOR train_ind := 1 TO nTrains DO
			IF Trains[train_ind].front_pos = tc_ind OR Trains[train_ind].rear_pos = tc_ind THEN
				tempBool := TRUE;
			END_IF
		END_FOR
		variable_list.TC_occupied[tc_ind] := tempBool;
	END_IF
END_FOR


